import { useEffect, useState } from 'react';
import { api } from '../services/api';

type Insumo = {
  id: number;
  nome: string;
  descricao: string;
};

export function Insumos() {
  const [insumos, setInsumos] = useState<Insumo[]>([]);
  const [novoInsumo, setNovoInsumo] = useState({ nome: '', quadescricao: '' });

  //Listar todos os produtos disponíveis para fabricação
  useEffect(() => {
    api.get('/insumos').then(res => setInsumos(res.data));
  }, []);

    const criarInsumo = async (e: React.FormEvent) => {
    e.preventDefault();
    await api.post('/insumo', novoInsumo);
    setNovoInsumo({ nome: '', descricao: '' });
    api.get('/insumo').then(res => setInsumos(res.data));
  };

  return (<>
    <h1>Inclusão de Insumos</h1>
      <form onSubmit={criarInsumo}>
        <input
          placeholder="Nome do Insumo"
          value={novoInsumo.nome}
          onChange={e => setNovoInsumo({ ...novoInsumo, nome: e.target.value })}
          required
        />
        <input
          placeholder="Descrição"
          value={novoInsumo.descricao}
          onChange={e => setNovoInsumo({ ...novoInsumo, descricao: e.target.value })}
        />
        <button type="submit">Adicionar Produto</button>
      </form>
    <div>
      <h1>Insumos</h1>
       <ul>
        {insumos.map(insumo => (
          <li key={insumo.id}>{insumo.nome}</li>
        ))}
      </ul>
    </div></>
  );
}
