import { useEffect, useState } from 'react';
import { api } from '../services/api';

type Insumo = {
  id: number;
  nome: string;
  descricao: string;
};

export function Insumos() {
  const [insumos, setInsumos] = useState<Insumo[]>([]);
  const [novoInsumo, setNovoInsumo] = useState<{
    nome: string;
    quantidadeEstoque: number;
    unidadeMedida: string;
    valor: number;
    dataNota: string;
    fornecedor: string;
    formaPagamento: string;
    categoria: string;
  }>({
    nome: '',
    quantidadeEstoque: 0,
    unidadeMedida: '',
    valor: 0,
    dataNota: '',
    fornecedor: '',
    formaPagamento: '',
    categoria: ''
  });

  //Listar todos os produtos disponíveis para fabricação
  useEffect(() => {
    api.get('/insumos').then(res => setInsumos(res.data));
  }, []);

  const criarInsumo = async (e: React.FormEvent) => {
    e.preventDefault();
    await api.post('/insumos', novoInsumo);
    setNovoInsumo({
      nome: '',
      quantidadeEstoque: 0,
      unidadeMedida: '',
      valor: 0,
      dataNota: '',
      fornecedor: '',
      formaPagamento: '',
      categoria: ''
    });
    api.get('/insumos').then(res => setInsumos(res.data));
  };

  return (<>
    <h1>Inclusão de Insumos</h1>
      <form onSubmit={criarInsumo}>
        <input
          placeholder="Nome do Insumo"
          value={novoInsumo.nome}
          onChange={e => setNovoInsumo({ ...novoInsumo, nome: e.target.value })}
          required
        />
        <input
          placeholder="Quantidade"
          type="number"
          value={novoInsumo.quantidadeEstoque}
          onChange={e => setNovoInsumo({ ...novoInsumo, quantidadeEstoque: Number(e.target.value) })}
        />
         <input
          placeholder="Unidades"
          value={novoInsumo.unidadeMedida}
          onChange={e => setNovoInsumo({ ...novoInsumo, unidadeMedida: e.target.value })}
        />
        <input
          placeholder="Valor"
          type="number"
          step="0.01"
          value={novoInsumo.valor}
          onChange={e => setNovoInsumo({ ...novoInsumo, valor: Number(e.target.value) })}
        />
        <input
          placeholder="Data da Nota"
          type="date"
          value={novoInsumo.dataNota}
          onChange={e => setNovoInsumo({ ...novoInsumo, dataNota: e.target.value })}
        />
        <input
          placeholder="Fornecedor"
          value={novoInsumo.fornecedor}
          onChange={e => setNovoInsumo({ ...novoInsumo, fornecedor: e.target.value })}
        />
        <select
          value={novoInsumo.formaPagamento}
          onChange={e => setNovoInsumo({ ...novoInsumo, formaPagamento: e.target.value })}
        >
          <option value="">Selecione a forma de pagamento</option>
          <option value="À vista">À vista</option>
          <option value="A prazo">A prazo</option>
        </select>
        <input
          placeholder="Categoria"
          value={novoInsumo.categoria}
          onChange={e => setNovoInsumo({ ...novoInsumo, categoria: e.target.value })}
        />
        <button type="submit">Adicionar Insumo</button>
      </form>
    <div>
      <h1>Insumos</h1>
       <ul>
        {insumos.map(insumo => (
          <li key={insumo.id}>{insumo.nome}</li>
        ))}
      </ul>
    </div></>
  );
}
