import { useEffect, useState } from 'react';
import { api } from '../services/api';

type Insumo = {
  id: number;
  nome: string;
  unidadeMedida?: string;
};

type InsumoNecessario = {
  id: number;
  quantidadeNecessaria: number;
  insumo: Insumo;
};

type Produto = {
  id: number;
  nome: string;
  descricao: string;
  insumosNecessarios: InsumoNecessario[];
};

export function Produtos() {
  const [produtos, setProdutos] = useState<Produto[]>([]);
  const [novoProduto, setNovoProduto] = useState({ nome: '', descricao: '' });

  const criarProduto = async (e: React.FormEvent) => {
    e.preventDefault();
    await api.post('/produtos', novoProduto);
    setNovoProduto({ nome: '', descricao: '' });
    api.get('/produtos').then(res => setProdutos(res.data));
  };

  useEffect(() => {
    api.get('/produtos').then(res => setProdutos(res.data));
  }, []);

  return (
    <div>
      <h1>Produtos</h1>
      <form onSubmit={criarProduto}>
        <input
          type="text"
          placeholder="Nome do produto"
          value={novoProduto.nome}
          onChange={e => setNovoProduto({ ...novoProduto, nome: e.target.value })}
        />
        <input
          type="text"
          placeholder="Descrição do produto"
          value={novoProduto.descricao}
          onChange={e => setNovoProduto({ ...novoProduto, descricao: e.target.value })}
        />
        <button type="submit">Criar Produto</button>
      </form>
      <ul>
        {produtos.map(produto => (
          <li key={produto.id} style={{ marginBottom: 20 }}>
            <strong>{produto.nome}</strong> - {produto.descricao}
            {produto.insumosNecessarios && produto.insumosNecessarios.length > 0 && (
              <ul>
                {produto.insumosNecessarios.map(inN => (
                  <li key={inN.id}>
                    {inN.insumo?.nome} ({inN.quantidadeNecessaria} {inN.insumo?.unidadeMedida || ''})
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
