import { useEffect, useState } from 'react';
import { api } from '../services/api';

type Produto = {
  id: number;
  nome: string;
};

export function OrdemProducao() {
  const [produtos, setProdutos] = useState<Produto[]>([]);
  const [produtoId, setProdutoId] = useState<number>(0);
  const [quantidade, setQuantidade] = useState<number>(1);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    api.get('/produtos').then(res => setProdutos(res.data));
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await api.post('/ordens-producao', {
        produtoId,
        quantidade,
      });
      setMsg('Ordem de produção criada com sucesso!');
    } catch (err) {
      setMsg('Erro ao criar ordem de produção.');
    }
  };

  return (
    <div>
      <h1>Criar Ordem de Fabricação</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Produto: </label>
          <select
            value={produtoId}
            onChange={e => setProdutoId(Number(e.target.value))}
            required
          >
            <option value="">Selecione</option>
            {produtos.map(p => (
              <option key={p.id} value={p.id}>{p.nome}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Quantidade: </label>
          <input
            type="number"
            min={1}
            value={quantidade}
            onChange={e => setQuantidade(Number(e.target.value))}
            required
          />
        </div>
        <button type="submit">Criar Ordem</button>
      </form>
      {msg && <p>{msg}</p>}
    </div>
  );
}
